import React, { useEffect, useRef } from "react";
import { Skills } from "../untils/SkillsFunction";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export default function Skill() {
  const titleRef = useRef(null); // Ref for the title
  const skillRefs = useRef([]); // Ref array for skill items

  useEffect(() => {
    // Animate the title
    gsap.fromTo(
      titleRef.current,
      { opacity: 0, y: -50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        ease: "power3.out",
        scrollTrigger: {
          trigger: titleRef.current,
          start: "top %", // When the top of the title reaches 80% of the viewport
          end: "top 10%", // When the top of the title reaches 10% of the viewport
          toggleActions: "play reverse play reverse", // Play and reverse when entering and leaving
          markers: false, // Disable markers for debugging
        },
      }
    );

    // Animate each skill item
    skillRefs.current.forEach((el, index) => {
      gsap.fromTo(
        el,
        { opacity: 0, y: 30 },
        {
          opacity: 1,
          y: 0,
          duration: 0.5,
          ease: "power3.out",
          scrollTrigger: {
            trigger: el,
            start: "top 10%", // Start animation when element enters the viewport (near bottom)
            end: "top 10%", // End the animation slightly after entering
            toggleActions: "play reverse play reverse", // Play and reverse when entering and leaving
            markers: true, // Disable markers for debugging
          },
        }
      );
    });
  }, []);

  return (
    <div className="bg-zinc-700 min-h-screen flex flex-col items-center pt-32">
      {/* Skills Title */}
      <h2
        ref={titleRef}
        className="lg:text-7xl md:text-5xl text-4xl font-bold mb-24 text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-teal-400"
      >
        Skills
      </h2>

      {/* Skills Grid */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-3 lg:grid-cols-4 gap-10 px-4 mb-7">
        {Skills.map((skill, index) => (
          <div
            key={index}
            ref={(el) => (skillRefs.current[index] = el)} // Store each skill item in the ref array
            className="flex flex-col items-center"
          >
            {/* Skill Icon */}
            <div className="w-16 h-16 sm:w-20 sm:h-20 md:w-32 md:h-32 lg:w-48 lg:h-48 mb-2">
              <img
                src={skill.imgSrc}
                alt={skill.name}
                className="w-full h-full object-contain rounded-full border-2 border-gray-300"
              />
            </div>
            {/* Skill Name */}
            <p className="text-center text-sm sm:text-base font-semibold text-white">
              {skill.name}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
