import React, { useState, useEffect, useRef } from 'react';
import { gsap } from 'gsap';

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);
  const logoRef = useRef(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // GSAP animation for logo and menu
  useEffect(() => {
    if (menuOpen) {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 1,
        y: 0,
        ease: 'power3.out',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 360,
        ease: 'power3.out',
      });
    } else {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 0,
        y: -50,
        ease: 'power3.in',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 0,
        ease: 'power3.in',
      });
    }
  }, [menuOpen]);

  return (
    <div className="fixed top-0 left-0 right-0 bg-zinc-700 text-white shadow-md z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo with GSAP animation */}
        <div className="flex items-center">
          <h1
            ref={logoRef}
            className="text-xl lg:text-5xl font-semibold"
          >
            Harsh
          </h1>
        </div>

        {/* Menu Icon for small screens */}
        <div className="sm:hidden md:hidden">
          <button onClick={toggleMenu} className="text-2xl">
            {menuOpen ? 'Close' : 'Open'}
          </button>
        </div>
      </div>

      {/* Mobile Menu with GSAP animation */}
      <div
        ref={menuRef}
        className={`sm:block md:hidden bg-zinc-800 p-5 text-center shadow-lg absolute top-full left-0 right-0 ${menuOpen ? 'block' : 'hidden'}`}
      >
        {/* Menu items can be added here if needed */}
      </div>
    </div>
  );
}
