import React, { useState, useEffect, useRef } from 'react';
import { FaBars } from 'react-icons/fa'; // Import the menu icon
import { AiOutlineClose } from 'react-icons/ai'; // Import the close icon for the menu
import { Link } from 'react-router-dom';
import { gsap } from 'gsap';

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);
  const logoRef = useRef(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // GSAP animation for logo and menu
  useEffect(() => {
    if (menuOpen) {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 1,
        y: 0,
        ease: 'power3.out',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 360,
        ease: 'power3.out',
      });
    } else {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 0,
        y: -50,
        ease: 'power3.in',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 0,
        ease: 'power3.in',
      });
    }
  }, [menuOpen]);

  return (
    <div className="fixed top-0 left-0 right-0 bg-zinc-700 text-white shadow-md z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center">
          <h1
            ref={logoRef}
            className="text-xl lg:text-5xl font-semibold"
          >
            Harsh
          </h1>
        </div>

        {/* Menu Icon (for small screens) */}
        <div className="sm:hidden md:hidden">
          <button onClick={toggleMenu} className="text-2xl">
            {menuOpen ? <AiOutlineClose /> : <FaBars />}
          </button>
        </div>

        {/* Navigation (Visible on medium screens and up) */}
        <div className="hidden sm:flex md:flex space-x-4">
          <Link to="/" className="text-lg hover:text-yellow-400">Home</Link>
          <Link to="/About" className="text-lg hover:text-yellow-400">About</Link>
          <Link to="/Services" className="text-lg hover:text-yellow-400">Services</Link>
          <Link to="Education" className="text-lg hover:text-yellow-400">Education</Link>
          <Link to="/Portfolio" className="text-lg hover:text-yellow-400">Portfolio</Link>
          <Link to="/Contact" className="text-lg hover:text-yellow-400">Contact</Link>
        </div>
      </div>

      {/* Mobile Menu (Visible when the menu is open on small screens) */}
      <div
        ref={menuRef}
        className={`sm:block md:hidden bg-zinc-800 p-5 text-center shadow-lg absolute top-full left-0 right-0 ${menuOpen ? 'block' : 'hidden'}`}
      >
        <Link to="/" className="block py-2 text-lg hover:text-yellow-400">Home</Link>
        <Link to="/about" className="block py-2 text-lg hover:text-yellow-400">About</Link>
        <Link to="/Skill" className="block py-2 text-lg hover:text-yellow-400">Skills</Link>
        <Link to="/Education" className="block py-2 text-lg hover:text-yellow-400">Education</Link>
        <Link to="/Portfolio" className="block py-2 text-lg hover:text-yellow-400">Portfolio</Link>
        <Link to="/Contact" className="block py-2 text-lg hover:text-yellow-400">Contact</Link>
      </div>
    </div>
  );
}
