import React, { useState } from 'react';
import { FaBars } from 'react-icons/fa'; // Import the menu icon
import { AiOutlineClose } from 'react-icons/ai'; // Import the close icon for the menu
import { Link } from 'react-router-dom';

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="fixed top-0 left-0 right-0 bg-zinc-700 text-white shadow-md z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center">
          {/* <img src={logo} alt="Harsh" className="h-8 mr-2" /> */}
          <h1 className="text-xl lg:text-5xl font-semibold">Harsh</h1>
        </div>

        {/* Menu Icon (for small screens) */}
        <div className="sm:hidden md:hidden">
          <button onClick={toggleMenu} className="text-2xl">
            {menuOpen ? <AiOutlineClose /> : <FaBars />}
          </button>
        </div>

        {/* Navigation (Visible on medium screens and up) */}
        <div className="hidden sm:flex md:flex space-x-4 ">
          <a h="/" className="text-lg">Home</a>
          <a h="/About" className="text-lg">About</a>
          <a h="/Services" className="text-lg">Services</a>
          <a h="Education" className="text-lg">Education</a> {/* Added Education */}
          <a h="/Portfolio" className="text-lg">Portfolio</a> {/* Added Portfolio */}
          <a h="/Contact" className="text-lg">Contact</a>
        </div>
      </div>

      {/* Mobile Menu (Visible when the menu is open on small screens) */}
      {menuOpen && (
        <div className="sm:block md:hidden bg-zinc-800 p-5 text-center shadow-lg absolute top-full left-0 right-0">
          <Link to="/" className="block py-2 text-lg">Home</Link>
          <Link to="/about" className="block py-2 text-lg">About</Link>
          <Link to="/Skills" className="block py-2 text-lg">SKills</Link>
          <Link to="/Education" className="block py-2 text-lg">Education</Link> {/* Added Education */}
          <Link to="/Portfolio" className="block py-2 text-lg">Portfolio</Link> {/* Added Portfolio */}
          <Link to="/Contact" className="block py-2 text-lg">Contact</Link>
        </div>
      )}
    </div>
  );
}
