import React, { useState, useEffect, useRef } from 'react';
import { FaBars } from 'react-icons/fa'; // Import the menu icon
import { AiOutlineClose } from 'react-icons/ai'; // Import the close icon for the menu
import { Link } from 'react-router-dom';
import { gsap } from 'gsap';

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);
  const logoRef = useRef(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // GSAP animation for page load (initial animation for the menu and logo)
  useEffect(() => {
    // Initial animation on page load
    gsap.from(logoRef.current, {
      duration: 1,
      rotate: 360,
      opacity: 0,
      ease: 'power3.out',
    });

    gsap.from(menuRef.current, {
      duration: 1,
      opacity: 0,
      y: -50,
      ease: 'power3.out',
    });
  }, []); // Empty dependency array ensures this runs only once on page load

  // GSAP animation for opening/closing menu
  useEffect(() => {
    if (menuOpen) {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 1,
        y: 0,
        ease: 'power3.out',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 360,
        ease: 'power3.out',
      });
    } else {
      gsap.to(menuRef.current, {
        duration: 0.5,
        opacity: 0,
        y: -50,
        ease: 'power3.in',
      });
      gsap.to(logoRef.current, {
        duration: 1,
        rotate: 0,
        ease: 'power3.in',
      });
    }
  }, [menuOpen]); // This runs when `menuOpen` state changes

  return (
    <div className="fixed top-0 left-0 right-0 bg-zinc-700 text-white shadow-md z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center">
          <h1
            ref={logoRef}
            className="text-xl lg:text-5xl font-semibold"
          >
            Harsh
          </h1>
        </div>

        {/* Menu Icon (for small screens) */}
        <div className="sm:hidden md:hidden">
          <button onClick={toggleMenu} className="text-2xl">
            {menuOpen ? <AiOutlineClose /> : <FaBars />}
          </button>
        </div>

        {/* Navigation (Visible on medium screens and up) */}
        <div className="hidden sm:flex md:flex space-x-4">
          <Link to="/" className="text-lg">Home</Link>
          <Link to="/About" className="text-lg">About</Link>
          <Link to="/Services" className="text-lg">Services</Link>
          <Link to="Education" className="text-lg">Education</Link>
          <Link to="/Portfolio" className="text-lg">Portfolio</Link>
          <Link to="/Contact" className="text-lg">Contact</Link>
        </div>
      </div>

      {/* Mobile Menu (Visible when the menu is open on small screens) */}
      <div
        ref={menuRef}
        className={`sm:block md:hidden bg-zinc-800 p-5 text-center shadow-lg absolute top-full left-0 right-0 ${menuOpen ? 'block' : 'hidden'}`}
      >
        <Link to="/" className="block py-2 text-lg">Home</Link>
        <Link to="/about" className="block py-2 text-lg">About</Link>
        <Link to="/Skills" className="block py-2 text-lg">Skills</Link>
        <Link to="/Education" className="block py-2 text-lg">Education</Link>
        <Link to="/Portfolio" className="block py-2 text-lg">Portfolio</Link>
        <Link to="/Contact" className="block py-2 text-lg">Contact</Link>
      </div>
    </div>
  );
}
