import React, { useRef, useEffect } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { Projects } from '../untils/skillFunction';

gsap.registerPlugin(ScrollTrigger);

export default function Portfolio() {
  const skillRefs = useRef([]);
  const titleRef = useRef(null);

  useEffect(() => {
    // Title animation (slide from left)
    gsap.fromTo(
      titleRef.current,
      { opacity: 0, x: -200 },
      {
        opacity: 1,
        x: 0,
        duration: 1.5,
        ease: "power3.out",
        scrollTrigger: {
          trigger: titleRef.current,
          start: "top 90%",
          end: "top 60%",
          toggleActions: "play none none reverse",
        },
      }
    );

    // Skills animation (slide from right)
    skillRefs.current.forEach((el, index) => {
      gsap.fromTo(
        el,
        { opacity: 0, x: 100 },
        {
          opacity: 1,
          x: 0,
          duration: 1,
          delay: index * 0.2,
          ease: "power3.out",
          scrollTrigger: {
            trigger: el,
            start: "top 85%",
            end: "top 60%",
            toggleActions: "play none none reverse",
          },
        }
      );
    });
    ScrollTrigger.refresh();
  }, []);

  return (
    <div className="bg-zinc-700 min-h-screen flex flex-col items-center pt-32">
      {/* Skills Title */}
      <h2
        ref={titleRef}
        className="lg:text-7xl md:text-5xl text-5xl font-bold mb-28 text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-teal-400"
      >
        Portfolio
      </h2>

      {/* Skills Grid */}
      <div className="grid grid-cols-2 md:grid-cols-2 lg:grid-cols-3 gap-5">
        {Projects.map((skill, index) => (
          <div
            key={index}
            ref={(el) => (skillRefs.current[index] = el)}
            className="flex flex-col items-center"
          >
            {/* Skill Icon */}
            <div className="w-16 h-16 sm:w-20 sm:h-20 md:w-32 md:h-32 lg:w-48 lg:h-48 mb-2">
              <img
                src={skill.imgSrc}
                alt={skill.name}
                className="w-full h-full object-contain rounded-full border-2 border-gray-300"
              />
            </div>
            {/* Skill Name */}
            <p className="text-center text-sm sm:text-base font-semibold text-white">
              {skill.name}
            </p>
            <p className="text-center text-sm sm:text-base font-semibold text-white">
              {skill.tech}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
